import React, { useEffect, useState } from 'react';
import { fetchReviews } from '../services/api'; // Ensure this is correctly implemented
import ReviewCard from './ReviewCard'; // New component for individual reviews
import './Reviews.css';

const Reviews = () => {
    const [reviews, setReviews] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const getReviews = async () => {
            try {
                const reviewsData = await fetchReviews(); // Adjust to include token if needed
                setReviews(reviewsData);
            } catch (err) {
                setError("Failed to load reviews.");
            }
        };
        getReviews();
    }, []);

    return (
        <div className="reviews-container">
            <h1>Your Reviews</h1>
            {error && <p className="error-message">{error}</p>}
            {reviews.map(review => (
                <ReviewCard key={review.id} review={review} />
            ))}
        </div>
    );
};

export default Reviews;
