import axios from 'axios';

const API_URL = 'http://localhost:8000'; // Adjust based on your backend URL

// Function to fetch actual reviews from the API
export const fetchReviews = async (token) => {
    const response = await axios.get(`${API_URL}/reviews`, {
        headers: {
            Authorization: `Bearer ${token}`
        }
    });
    return response.data;
};

// Function to fetch mock reviews from the API
export const fetchMockReviews = async () => {
    const response = await axios.get(`${API_URL}/mock/reviews`);
    return response.data;
};

// Function to log in and get an access token
export const loginUser = async (credentials) => {
    const response = await axios.post(`${API_URL}/token`, credentials);
    return response.data;
};
