import React from 'react';
import { GoogleLogin } from '@react-oauth/google';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import './Login.css'; // Import the CSS file

const apiBaseUrl = 'http://localhost:3000'; // Define the API base URL

const LoginPage = () => {
    const navigate = useNavigate(); // Initialize the navigate function

    const handleGoogleLogin = async (credentialResponse) => {
        try {
            // Call your API endpoint with the credential response
            const response = await axios.post(`${apiBaseUrl}/api/google-login`, {
                id_token: credentialResponse.credential,
            });
            console.log(response.data);
            // Handle the API response, e.g., store user data or redirect to dashboard

            // Redirect to reviews page after successful login
            navigate('/reviews'); // Change '/reviews' to your actual reviews page route
        } catch (error) {
            console.error('Error logging in with Google:', error);
        }
    };

    return (
        <div className="login-page">
            <h1>Sign In</h1>
            <GoogleLogin
                onSuccess={handleGoogleLogin}
                onError={(error) => console.error(error)}
                className="google-login-button"
            />
        </div>
    );
};

export default LoginPage;
