import React, { useEffect, useState } from 'react';
import { fetchReviews, fetchMockReviews } from '../services/api'; // Combined API service
import './Reviews.css';

const Reviews = () => {
    const [reviews, setReviews] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const getReviews = async () => {
            const token = localStorage.getItem('token');
            try {
                const reviewsData = await fetchReviews(token);
                setReviews(reviewsData);
            } catch (err) {
                console.error("Failed to fetch reviews from API:", err);
                setError("Loading mock data...");

                try {
                    const mockReviewsData = await fetchMockReviews();
                    setReviews(mockReviewsData);
                } catch (mockErr) {
                    console.error("Failed to fetch mock reviews:", mockErr);
                    setError("Failed to load mock data.");
                }
            }
        };

        getReviews();
    }, []);

    return (
        <div className="reviews-container">
            <h1>Your Reviews</h1>
            {error && <p className="error-message">{error}</p>}
            {reviews.map(review => (
                <div key={review.id} className="review-card">
                    <h3>{review.author}</h3>
                    <p>Rating: {review.rating}</p>
                    <p>{review.comment}</p>
                </div>
            ))}
        </div>
    );
};

export default Reviews;
